import React from "react"


class InputArea extends React.Component{

  constructor(){
    super()
    this.state={
      memeImages:[],
      randomImage:"https://i.imgflip.com/30b1gx.jpg",
      counter:1,
      inputBoxID:0
    }
    this.changeImage = this.changeImage.bind(this)
    this.addTextBox = this.addTextBox.bind(this)
    this.dragOverImage = this.dragOverImage.bind(this)

  }

   changeImage(){
     console.log("Clicked");
    this.setState(currentState=>{
      return{
      counter:currentState.counter + 1,
      randomImage:currentState.memeImages[currentState.counter].url
    }
    }
  )
  }



  componentWillMount(){
    fetch("https://api.imgflip.com/get_memes")
    .then(response => response.json())
    .then(response=>{
      const {data} = response
      this.setState({
        memeImages:data.memes
      })
    })
  }

  addTextBox(){
    return(
      <input className="inputBox" type="text" placeholder="Top Text" />
    )
  }

  dragOverImage(event){
    console.log(event.pageX());
  }

  dragStart(event){
    console.log("Drag Start");
  }

  render(){
      return(
        <div>
        <div className="inputBoxContainer">
        <input key={this.state.inputBoxID} className="inputBox" type="text" placeholder="Drag me" draggable onDragStart={(e)=>{this.dragStart(e)}}  />
        <button className="generateButton">Gen</button>
        </div>
        <img className="memeImage" src={this.state.randomImage} onDragOver={this.dragOverImage}/>
        <button onClick={this.changeImage}> Next Image</button>
        </div>
      )
  }
}

export default InputArea
