import React from "react"
import MemeImage from "./MemeImage.js"


class InputArea extends React.Component{

  constructor(){
    super()
    this.state={
      memeImages:[],
      randomImage:"https://i.imgflip.com/30b1gx.jpg",
      counter:1
    }
    this.changeImage = this.changeImage.bind(this)
  }

   changeImage(){
    this.setState(currentState=>{
      currentState.counter = currentState.counter + 1
      currentState.randomImage = currentState.memeImages[currentState.counter].url
    })
  }



  componentWillMount(){
    fetch("https://api.imgflip.com/get_memes")
    .then(response => response.json())
    .then(response=>{
      const {data} = response
      this.setState({
        memeImages:data.memes
      })
    })
  }

  render(){
      return(
        <div>
        <div className="inputBoxContainer">
        <input className="inputBox" type="text" placeholder="Top Text" />
        <input className="inputBox" type="text" placeholder="Bottom Text" />
        <button className="generateButton">Gen</button>
        </div>
        <MemeImage image={this.state.randomImage} nextImage={this.changeImage}/>
        </div>
      )
  }
}

export default InputArea
